import React, { useState, useEffect} from 'react';
import Slider from './Slider1'; 
import {
  Card,
  CardContainer,
  CardText,
  CardTitle,
  CardSubtitle,
  CardButton
} from './UniversityCard.styles';

const UniversityCard = ({ institute, course, category, minRank, maxRank, onApply}) => {
  const [showFullName, setShowFullName] = useState(false);
  const [firstPart, rest] = institute.split(',', 2);

  return (
    <Card>
      <CardText>
        <CardTitle>
          {firstPart}
          {showFullName && `,${rest}`}
          {!showFullName && rest && (
            <button
              onClick={() => setShowFullName(true)}
              style={{ color: 'blue', cursor: 'pointer' }}
            >
              .Read More
            </button>
          )}
        </CardTitle>
        <CardSubtitle>Course: {course}</CardSubtitle>
        <CardSubtitle>Category: {category}</CardSubtitle>
        <CardSubtitle>Clossing Rank: {maxRank}</CardSubtitle>
        {/* <CardButton onClick={onApply}>Apply Now</CardButton> */}
      </CardText>
    </Card>
  );
};

const UniversityCardList = ({ universities = [] }) => {
  const [NeetfilteredUniversities, setNeetFilteredUniversities] = useState(universities);

  const handleRangeChange = (range) => {
    setNeetFilteredUniversities(
      universities.filter((college) => {
        const roundedmaxRank = Math.floor(college.maxRank);
        return (
          roundedmaxRank >= range[0] &&
          roundedmaxRank <= range[1]
        );
      })
    );
  };

  useEffect(() => {
    if (universities.length > 0) {
      const maxRanks = universities.map((college) => Math.floor(college.maxRank));
      const maxmaxRank = Math.max(...maxRanks);
      handleRangeChange([0, maxmaxRank]); 
    }
  }, [universities]);

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      {/* Slider Section */}
      <div style={{ width: '100%', maxWidth: '600px', marginBottom: '20px' }}>
        <Slider universities={universities} onRangeChange={handleRangeChange} />
      </div>
      
      <CardContainer>
        {Array.isArray(NeetfilteredUniversities) && NeetfilteredUniversities.length > 0 ? (
          NeetfilteredUniversities.map((college) => (
            <UniversityCard
              key={college['_id']}
              institute={college['Allotted Institute']}
              maxRank={Math.floor(college['maxRank'])}
              onApply={() => {
                const url = college['Website URL'];
                const formattedURL =
                  url.startsWith('http://') || url.startsWith('https://')
                    ? url
                    : `http://${url}`;
                window.open(formattedURL, '_blank');
              }}
            />
          ))
        ) : (
          <p>No colleges found</p>
        )}
      </CardContainer>
    </div>
  );
};

export default UniversityCardList;
