import React, { useState, useEffect } from 'react';
import ReactSlider from 'react-slider';

const sliderStyles = {
  slidecontainer: {
    width: '100%',
    margin: '0 auto',
  },
  slider: {
    WebkitAppearance: 'none', /* Override default CSS styles */
    appearance: 'none',
    width: '100%', /* Full-width */
    height: '25px', /* Specified height */
    background: '#d3d3d3', /* Grey background */
    outline: 'none', /* Remove outline */
    opacity: '0.7', /* Set transparency */
    transition: 'opacity .2s', /* Transition on hover */
  },
  thumb: {
    WebkitAppearance: 'none', /* Override default look */
    appearance: 'none',
    width: '25px', /* Handle width */
    height: '25px', /* Handle height */
    background: '#04AA6D', /* Green background */
    cursor: 'pointer', /* Cursor on hover */
    borderRadius: '50%',
    position: 'relative',
    top: '-10px', /* Adjust the position of the thumb */
    boxShadow: '0 0 6px rgba(0,0,0,0.3)', /* Shadow for better visibility */
  },
  rangeValues: {
    display: 'flex',
    justifyContent: 'space-between',
    fontSize: '14px',
    marginTop: '10px',
  },
};

const Slider = ({ universities, onRangeChange }) => {
  const [percentileRange, setPercentileRange] = useState([0, 100]);

  useEffect(() => {
    if (universities.length > 0) {
      const percentiles = universities.map((college) => Math.floor(college.percentile));
      const minPercentile = Math.min(...percentiles);
      const maxPercentile = Math.max(...percentiles);
      setPercentileRange([minPercentile, maxPercentile]);
    }
  }, [universities]);

  const handleChange = (value) => {
    const newRange = [value, percentileRange[1]];
    setPercentileRange(newRange);
    onRangeChange(newRange);
  };

  return (
    <div style={sliderStyles.slidecontainer}>
      <ReactSlider
        min={percentileRange[0]}
        max={percentileRange[1]}
        value={percentileRange[0]}
        onChange={handleChange}
        renderTrack={(props) => <div {...props} style={sliderStyles.slider} />}
        renderThumb={(props) => <div {...props} style={sliderStyles.thumb} />}
      />
      <div style={sliderStyles.rangeValues}>
        <span>{percentileRange[0]}</span>
        <span>{percentileRange[1]}</span>
      </div>
    </div>
  );
};

export default Slider;
