import React, { useState } from "react";
import { Tab, Tabs, TabPanel } from "react-tabs";
import "react-tabs/style/react-tabs.css";
import FundCards from "./UniversityCard";
import SectionTitle from "./SectionTitle";
import jsPDF from "jspdf";
import "jspdf-autotable";

export default function FundsTabs({ universities }) {
  const [pdfPreviewUrl, setPdfPreviewUrl] = useState(null);

  // Base64 or URL of your images
  const logoLeft = `${process.env.PUBLIC_URL}/logo192.png`; // Replace with your base64 string for the left logo
  const logoRight = `${process.env.PUBLIC_URL}/logo192.png`;
  const newImage = `${process.env.PUBLIC_URL}/logo192.png`; // Replace with your base64 string for the right logo

  const handleGeneratePDF = () => {
    const doc = new jsPDF('p', 'pt', 'a4');
    const pageWidth = doc.internal.pageSize.getWidth();

    // Function to add header with logos on each page
    const addHeader = () => {
      // Add left logo
      doc.addImage(logoLeft, 'PNG', 40, 10, 50, 50); // Adjust position and size as needed
      // Add right logo
      doc.addImage(logoRight, 'PNG', pageWidth - 90, 10, 50, 50); // Adjust position and size as needed
      
      // Set font size for title
      doc.setFontSize(16);
      const title = 'Indiaâ€™s Leading Educational Web Portal & App';
      const titleWidth = doc.widthOfString(title, { size: 16 });
      const centerX = doc.getWidth() / 2; 
      doc.text(title, centerX - titleWidth / 2, 70);
      
      // Add the new image below the title
      const newImageY = 85; // Adjust y-coordinate as needed
      doc.addImage(newImage, 'PNG', centerX - 25, newImageY, 50, 50); // Centered below the title
    
      // Set font size for subtitle and description
      doc.setFontSize(12);
      const subtitle = 'Counselling | Admissions | KG to PG | Aptitude Tests Mock Exams | Jobs | Skills | India | Abroad';
      const subtitleY = 150; // Adjust y-coordinate for subtitle
      doc.text(subtitle, 40, subtitleY);
      
      // Set red color for specific words
      const redWords = ['Education', 'Mock Exams', 'Abroad'];
      const redColor = [255, 0, 0]; // RGB for red
      
      // Draw the subtitle with specific words in red
      const words = subtitle.split('|');
      let currentY = subtitleY;
      
      words.forEach((word, index) => {
        // Trim spaces and set text color
        const trimmedWord = word.trim();
        if (redWords.includes(trimmedWord)) {
          doc.setTextColor(...redColor); // Set to red
        } else {
          doc.setTextColor(0, 0, 0); // Set to black
        }
    
        // Add each word to the document
        doc.text(trimmedWord, 40, currentY);
        currentY += 10; // Adjust spacing between lines
      });
      
      // Add contact information below the subtitle
      doc.setTextColor(0, 0, 0); // Reset to black
      doc.text('Web: www.vidyarthimitra.org | Ph. +91 77200 25900;', 40, currentY + 10);
    };
    

    // Add header to the first page
    addHeader();

    // Add student information (Assuming the details are hardcoded or can be passed as props)
    doc.setFontSize(14);
    doc.text(`Student Name: Rakhi`, 40, 140);
    doc.text(`Course Preferences: Bachelor of Pharmacy`, 40, 160);
    doc.text(`University Preference: Pune & Mumbai University`, 40, 180);
    doc.text(`MHT-CET Score : 79.22`, 40, 200);
    doc.text(`Pune & Mumbai College Preferences List`, 40, 220);

    // Add table headers
    const columns = [
      { header: 'Sr.No', dataKey: 'srNo' },
      { header: 'College Code', dataKey: 'collegeCode' },
      { header: 'College Name', dataKey: 'collegeName' },
      { header: 'Address', dataKey: 'address' },
      { header: 'Status', dataKey: 'status' },
      { header: 'District', dataKey: 'district' }
    ];

    // Map the data to match the table structure
    const rows = universities.map((college, index) => ({
      srNo: index + 1,
      collegeCode: college['college code'], // Replace with actual field name
      collegeName: college['College Name'],
      address: college['Address'], // Replace with actual field name
      status: college['Institute Status'], // Replace with actual field name
      district: college['District'] // Replace with actual field name
    }));

    // Add table with university data
    doc.autoTable({
      startY: 240, // Start position for the table
      head: [columns.map(col => col.header)],
      body: rows.map(row => columns.map(col => row[col.dataKey])),
      margin: { top: 10 },
      didDrawPage: (data) => {
        // Re-add header on every page
        addHeader();
      }
    });

    // Add footer
    doc.text('Note: The above list is not an allotment or admission list. It is just only predictions based on score, category, previous year trending cut-offs, Institute or College status, all ranks and many other parameters.', 40, doc.lastAutoTable.finalY + 40);

    // Create Blob URL for PDF preview
    const pdfBlob = doc.output('blob');
    const pdfUrl = URL.createObjectURL(pdfBlob);
    setPdfPreviewUrl(pdfUrl);
  };

  const handleDownloadPDF = () => {
    const doc = new jsPDF('p', 'pt', 'a4');
    // Add your PDF generation code here as above (you can reuse the handleGeneratePDF code)
    doc.save('Filtered_Colleges_List.pdf');
  };

  return (
    <div>
    <Tabs className="sm:mt-0 mt-14">
      <div className="my-8 flex flex-col items-center">
        <SectionTitle
          title="Most Popular Colleges"
          classes="text-center text-2xl font-bold sm:text-3xl" // Corrected font size
        />
        <div className="w-full flex justify-end sm:justify-center sm:pr-0 pr-4 mt-4">
          <button
            className="text-[15px] font-bold py-2 px-4 bg-green-500 text-white rounded"
            onClick={handleGeneratePDF}
          >
            Preview PDF
          </button>
        </div>
        </div>
        <TabPanel>
          <div className="flex justify-center">
            {universities.length > 0 ? (
              <FundCards universities={universities} />
            ) : (
              <p className="text-center text-gray-600">No colleges found</p>
            )}
          </div>
        </TabPanel>
      </Tabs>

      {/* PDF Preview Modal */}
      {pdfPreviewUrl && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded-lg relative max-w-3xl w-full">
            <button
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-800"
              onClick={() => setPdfPreviewUrl(null)}
            >
              âœ–
            </button>
            <h2 className="text-xl font-bold mb-4">PDF Preview</h2>
            <iframe
              src={pdfPreviewUrl}
              width="100%"
              height="500px"
              title="PDF Preview"
              className="border"
            />
            <div className="text-right mt-4">
              <button
                className="text-[15px] font-bold py-2 px-4 bg-blue-500 text-white rounded"
                onClick={handleDownloadPDF}
              >
                Download PDF
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
