import React, { useState } from "react";
import { Tab, Tabs, TabPanel } from "react-tabs";
import "react-tabs/style/react-tabs.css";
import FundCards from "./UniversityCard";
import SectionTitle from "./SectionTitle";
import jsPDF from "jspdf";
import "jspdf-autotable";

export default function FundsTabs({ universities }) {
  const [pdfPreviewUrl, setPdfPreviewUrl] = useState(null);

  const selectedUniversity = universities[0];

  const logoLeft = `${process.env.PUBLIC_URL}/logo192.png`; 
  const logoRight = `${process.env.PUBLIC_URL}/logo192.png`; 
  const newLogo = `${process.env.PUBLIC_URL}/logo192.png`; // Update with your new logo path

  const handleGeneratePDF = () => {
    const doc = new jsPDF('p', 'pt', 'a4');
    const pageWidth = doc.internal.pageSize.getWidth();
    
    const addHeader = () => {
      doc.addImage(logoLeft, 'PNG', 40, 10, 50, 50); 
      doc.addImage(logoRight, 'PNG', pageWidth - 90, 10, 50, 50); 
    };
  
    const addFirstPageContent = () => {
      doc.setFontSize(16);
      const title = 'India’s Leading Educational Web Portal & App';
      const titleWidth = doc.getTextWidth(title, { size: 16 });
      const centerX = pageWidth / 2; 
      doc.text(title, centerX - titleWidth / 2, 70);
  
      doc.addImage(newLogo, 'PNG', centerX - 25, 85, 50, 50); 
  
      doc.setFontSize(12);
      const subtitle1 = 'Counselling | Admissions | KG to PG | Aptitude Tests';
      const subtitle2 = 'Mock Exams | Jobs | Skills | India | Abroad';
      const subtitleY = 140; 
  
      doc.text(subtitle1, centerX - doc.getTextWidth(subtitle1, { size: 12 }) / 2, subtitleY);
      doc.text(subtitle2, centerX - doc.getTextWidth(subtitle2, { size: 12 }) / 2, subtitleY + 15);
      
      doc.text('Web: www.vidyarthimitra.org | Ph. +91 77200 25900;', centerX - doc.getTextWidth('Web: www.vidyarthimitra.org | Ph. +91 77200 25900;', { size: 12 }) / 2, subtitleY + 45);
      
      doc.setDrawColor(0, 0, 0);
      doc.setLineWidth(1);
      doc.line(40, subtitleY + 55, pageWidth - 40, subtitleY + 55);
      
      const lineHeight = 20; 
      doc.setFontSize(14);
      doc.text(`Student Name: Rakhi`, 40, subtitleY + 70);
      doc.text(`Course Preferences: ${selectedUniversity['Branch Name']}`, 40, subtitleY + 70 + lineHeight);
      doc.text(`City Preference: ${selectedUniversity['District']}`, 40, subtitleY + 70 + lineHeight * 2);
      doc.text(`MHT-CET Score: ${selectedUniversity['percentile']}`, 40, subtitleY + 70 + lineHeight * 3);
      
      const preferencesTitle = `${selectedUniversity['District']} College Preferences List`;
      doc.setFontSize(14);
      doc.setFont(undefined, 'underline');
      doc.text(preferencesTitle, centerX - doc.getTextWidth(preferencesTitle, { size: 14 }) / 2, subtitleY + 70 + lineHeight * 4);
      doc.setFont(undefined, 'normal');
    };
  
    // Add content
    addHeader();
    addFirstPageContent();
  
    const startY = doc.lastAutoTable.finalY + 20; 
  
    // Check if universities have data
    if (universities.length === 0) {
      doc.text("No university data available.", 40, startY);
    } else {
      const columns = [
        { header: 'Sr.No', dataKey: 'srNo' },
        { header: 'College Code', dataKey: 'collegeCode' },
        { header: 'College Name', dataKey: 'collegeName' },
        { header: 'Address', dataKey: 'address' },
        { header: 'Status', dataKey: 'status' },
        { header: 'District', dataKey: 'district' }
      ];
  
      const rows = universities.map((college, index) => ({
        srNo: index + 1,
        collegeCode: college['college code'], 
        collegeName: college['College Name'],
        address: college['Address'], 
        status: college['Institute Status'], 
        district: college['District'] 
      }));
  
      doc.autoTable({
        startY: 240,
        head: [columns.map(col => col.header)],
        body: rows.map(row => columns.map(col => row[col.dataKey])),
        margin: { top: 10 },
        /*didDrawPage: (data) => {
          addHeader(); 
          const currentPageY = doc.internal.getCurrentPageInfo().pageHeight;
          if (currentPageY - data.cursor.y < 100) {
            doc.addPage();
          }
        }*/
      });
    }
  
    const footerText = 'Note: The above list is not an allotment or admission list. It is just predictions based on score, category, previous year trending cut-offs, Institute or College status, all ranks and many other parameters.';
    
    // Ensure there is space for footer
    const footerY = doc.internal.pageSize.height - 40; // 40 units from the bottom
    const remainingSpace = footerY - doc.lastAutoTable.finalY;
  
    if (remainingSpace < 20) {
      doc.addPage();
    }
  
    doc.text(footerText, 40, footerY);
  
    const pdfBlob = doc.output('blob');
    const pdfUrl = URL.createObjectURL(pdfBlob);
    setPdfPreviewUrl(pdfUrl);
  };  

  const handleDownloadPDF = () => {
    const doc = new jsPDF('p', 'pt', 'a4');
    doc.save('Filtered_Colleges_List.pdf');
  };

  return (
    <div>
      <Tabs className="sm:mt-0 mt-14">
        <div className="my-8 flex flex-col items-center">
          <SectionTitle
            title="Most Popular Colleges"
            classes="text-center text-2xl font-bold sm:text-3xl"
          />
          <div className="w-full flex justify-end sm:justify-center sm:pr-0 pr-4 mt-4">
            <button
              className="text-[15px] font-bold py-2 px-4 bg-green-500 text-white rounded"
              onClick={handleGeneratePDF}
            >
              Preview PDF
            </button>
          </div>
        </div>
        <TabPanel>
          <div className="flex justify-center">
            {universities.length > 0 ? (
              <FundCards universities={universities} />
            ) : (
              <p className="text-center text-gray-600">No colleges found</p>
            )}
          </div>
        </TabPanel>
      </Tabs>

      {pdfPreviewUrl && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded-lg relative max-w-3xl w-full">
            <button
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-800"
              onClick={() => setPdfPreviewUrl(null)}
            >
              ✖
            </button>
            <h2 className="text-xl font-bold mb-4">PDF Preview</h2>
            <iframe
              src={pdfPreviewUrl}
              width="100%"
              height="500px"
              title="PDF Preview"
              className="border"
            />
            <div className="text-right mt-4">
              <button
                className="text-[15px] font-bold py-2 px-4 bg-blue-500 text-white rounded"
                onClick={handleDownloadPDF}
              >
                Download PDF
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
