// src/components/SidePanel.jsx
import React from 'react';
import './SidePanel.css'; // Ensure you have a corresponding CSS file

const SidePanel = ({ isOpen, onClose, isLoggedIn, userDetails, onLogout }) => {
  if (!isOpen) return null; // Don't render if the panel is not open

  return (
    <div className="side-panel">
      <button className="close-btn" onClick={onClose}>Ã—</button>
      {isLoggedIn ? (
        <div className="user-details">
          <h3>Welcome, {userDetails.name}</h3>
          <p>Email: {userDetails.email}</p>
          {/* Add more user details as needed */}
          <button onClick={onLogout}>Logout</button>
        </div>
      ) : (
        <div className="login-signup">
          <h2>Login / Sign Up</h2>
          {/* Add your login modal or form component here */}
          <button onClick={() => console.log('Login clicked')}>Login</button>
          <button onClick={() => console.log('Sign Up clicked')}>Sign Up</button>
        </div>
      )}
    </div>
  );
};

export default SidePanel;
