import React, { useState } from 'react';
import './SidePanel.css'; 
import LoginModal from "./LoginModal"; // Import the modal

const SidePanel = ({ isOpen, onClose, isLoggedIn, userDetails, onLogout }) => {
  const [showModal, setShowModal] = useState(false); // Modal visibility state
  const [user, setUser] = useState(userDetails); // Manage user state

  const handleLoginSuccess = (userData) => {
    setUser(userData); // Update user details after successful login/signup
  };

  if (!isOpen) return null; 

  return (
    <>
      {/* Side Panel Content */}
      <div className={`side-panel ${isOpen ? 'open' : ''}`}>
        <button className="close-btn" onClick={onClose}>Ã—</button>

        {user ? (
          <div className="user-details">
            <h3>Welcome, {user.name}</h3>
            <p>Email: {user.email}</p>
            <button onClick={onLogout} className="logout-btn">Logout</button>
          </div>
        ) : (
          <div className="login-signup">
            <h2>Login / Sign Up</h2>
            <button onClick={() => setShowModal(true)} className="login-btn">Login</button>
            <button onClick={() => setShowModal(true)} className="signup-btn">Sign Up</button>
          </div>
        )}
      </div>

      {/* Modal for Login/Signup */}
      <LoginModal
        isOpen={showModal}
        onClose={() => setShowModal(false)}
        onLoginSuccess={handleLoginSuccess}
      />
    </>
  );
};

export default SidePanel;
