import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginModal.css';

const LoginTypeSelector = ({ type, handleLoginTypeChange }) => (
  <div className="login-type">
    <label>
      <input
        type="radio"
        value="student"
        checked={type === 'student'}
        onChange={handleLoginTypeChange}
      />
      Student
    </label>
    <label>
      <input
        type="radio"
        value="admin"
        checked={type === 'admin'}
        onChange={handleLoginTypeChange}
      />
      Admin
    </label>
  </div>
);

const LoginForm = ({
  isRegistering,
  email,
  setEmail,
  password,
  setPassword,
  name,
  setName,
  course,
  setCourse,
  percentile,
  setPercentile,
  handleFormSubmit,
  isLoading,
}) => (
  <div className="login-form">
    {isRegistering && (
      <>
        <input
          required
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          required
          type="text"
          placeholder="Course"
          value={course}
          onChange={(e) => setCourse(e.target.value)}
        />
        <input
          required
          type="number"
          placeholder="Percentile"
          value={percentile}
          onChange={(e) => setPercentile(e.target.value)}
        />
      </>
    )}
    <input
      required
      type="email"
      placeholder="Email"
      value={email}
      onChange={(e) => setEmail(e.target.value)}
    />
    <input
      required
      type="password"
      placeholder="Password"
      value={password}
      onChange={(e) => setPassword(e.target.value)}
    />
    <button onClick={handleFormSubmit} disabled={isLoading}>
      {isRegistering ? 'Register' : 'Login'}
    </button>
    {isLoading && <p>Loading...</p>}
  </div>
);

const LoginModal = ({ onLogin }) => {
  const [type, setType] = useState('student');
  const [isRegistering, setIsRegistering] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [course, setCourse] = useState('');
  const [percentile, setPercentile] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const navigate = useNavigate();

  const handleLoginTypeChange = (e) => setType(e.target.value);

  const handleFormSubmit = async () => {
    setIsLoading(true);

    if (isRegistering) {
      // Registration logic
      const formData = { name, email, password, type, course, percentile: parseInt(percentile, 10) };
      try {
        const response = await fetch('http://localhost:4000/api/register', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(formData),
        });
        const data = await response.json();
        if (data.success) {
          alert('Registered successfully!');
          onLogin(data.user); // Triggering onLogin after registration
          navigate('/'); // Redirect after success
        } else {
          alert('Registration failed: ' + data.message);
        }
      } catch (error) {
        console.error('Error during registration:', error);
        alert('Registration failed. Please try again.');
      }
    } else {
      try {
        const logindata = await loginUser({ email, password, type });

        if (logindata.success && logindata.user.type === type) {
          localStorage.setItem('user', JSON.stringify(logindata.user));
          onLogin(logindata.user); // Triggering onLogin after login

          switch (logindata.user.type) {
            case 'admin':
              navigate('/admin', { replace: true });
              break;
            case 'student':
              navigate('/', { replace: true });
              break;
            default:
              navigate('/', { replace: true });
          }
        } else {
          alert('Login failed! Invalid credentials.');
        }
      } catch (error) {
        console.error('Error during login:', error);
        alert('Login failed. Please check your server logs.');
      }
    }
    setIsLoading(false);
  };

  const loginUser = async (userData) => {
    try {
      const response = await fetch('http://localhost:4000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userData),
      });

      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);

      return await response.json();
    } catch (error) {
      console.error('Error during login:', error);
      throw error;
    }
  };

  return (
    <div className="login-modal">
      <h2>{isRegistering ? 'Register' : 'Login'}</h2>
      <LoginTypeSelector type={type} handleLoginTypeChange={handleLoginTypeChange} />
      <LoginForm
        isRegistering={isRegistering}
        email={email}
        setEmail={setEmail}
        password={password}
        setPassword={setPassword}
        name={name}
        setName={setName}
        course={course}
        setCourse={setCourse}
        percentile={percentile}
        setPercentile={setPercentile}
        handleFormSubmit={handleFormSubmit}
        isLoading={isLoading}
      />
      <a
        href="/"
        onClick={(e) => {
          e.preventDefault();
          setIsRegistering(!isRegistering);
        }}
        style={{ cursor: 'pointer', color: '#007bff', textDecoration: 'underline' }}
      >
        {isRegistering ? 'Already have an account? Login' : 'New here? Register'}
      </a>
    </div>
  );
};

export default LoginModal;
