import React, { useState } from 'react';
import './SidePanel.css'; // Ensure you have a corresponding CSS file for custom styles

function SidePanel({ isOpen, onClose, isLoggedIn, onLogin, onLogout, onSignup }) {
  const [isLoginMode, setIsLoginMode] = useState(true); // Toggle between login and signup modes
  const [formData, setFormData] = useState({ username: '', password: '', confirmPassword: '' });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (isLoginMode) {
      // Call login handler
      onLogin();
    } else {
      // Handle signup, include password validation, etc.
      if (formData.password === formData.confirmPassword) {
        onSignup();
      } else {
        alert("Passwords don't match");
      }
    }
  };

  return (
    <div className={`fixed inset-0 z-50 transition-transform ${isOpen ? "translate-x-0" : "-translate-x-full"} bg-gray-100 shadow-lg`}>
      <div className="flex justify-between p-4 border-b">
        <h2 className="text-xl font-bold">{isLoggedIn ? "Profile" : isLoginMode ? "Login" : "Sign Up"}</h2>
        <button onClick={onClose} className="text-lg">X</button>
      </div>

      {isLoggedIn ? (
        <div className="p-4">
          <h3 className="text-lg">Welcome, User!</h3>
          <button onClick={onLogout} className="mt-4 p-2 bg-red-500 text-white rounded">Logout</button>
        </div>
      ) : (
        <div className="p-4">
          <form onSubmit={handleFormSubmit}>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm mb-2" htmlFor="username">Username</label>
              <input
                id="username"
                name="username"
                type="text"
                value={formData.username}
                onChange={handleInputChange}
                className="w-full px-3 py-2 border rounded"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm mb-2" htmlFor="password">Password</label>
              <input
                id="password"
                name="password"
                type="password"
                value={formData.password}
                onChange={handleInputChange}
                className="w-full px-3 py-2 border rounded"
                required
              />
            </div>

            {!isLoginMode && (
              <div className="mb-4">
                <label className="block text-gray-700 text-sm mb-2" htmlFor="confirmPassword">Confirm Password</label>
                <input
                  id="confirmPassword"
                  name="confirmPassword"
                  type="password"
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border rounded"
                  required
                />
              </div>
            )}

            <div className="flex justify-between items-center">
              <button type="submit" className="px-4 py-2 bg-blue-500 text-white rounded">
                {isLoginMode ? "Login" : "Sign Up"}
              </button>

              <button
                type="button"
                onClick={() => setIsLoginMode(!isLoginMode)}
                className="text-sm text-gray-500 underline">
                {isLoginMode ? "Don't have an account? Sign Up" : "Already have an account? Login"}
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}

export default SidePanel;
