import React, { useState, useEffect } from 'react';
import ReactSlider from 'react-slider';
import { sliderStyles } from './styles'; // Import slider styles
import {
  Card,
  CardContainer,
  CardText,
  CardTitle,
  CardSubtitle,
  CardButton
} from './UniversityCard.styles';

const UniversityCard = ({ name, city, branch, percentile, onApply }) => {
  const [showFullName, setShowFullName] = useState(false);
  const [firstPart, rest] = name.split(',', 2);

  return (
    <Card>
      <CardText>
        <CardTitle>
          {firstPart} 
          {showFullName && `,${rest}`} 
          {!showFullName && rest && (
            <button
              onClick={() => setShowFullName(true)}
              style={{ color: 'blue', cursor: 'pointer' }}
            >
              .Read More
            </button>
          )}
        </CardTitle>
        <CardSubtitle>City: {city}</CardSubtitle>
        <CardSubtitle>Branch: {branch}</CardSubtitle>
        <CardSubtitle>Percentile: {percentile}</CardSubtitle>
        {/* <CardButton onClick={onApply}>Apply Now</CardButton> */}
      </CardText>
    </Card>
  );
};

const UniversityCardList = ({ universities }) => {
  const [percentileRange, setPercentileRange] = useState([0, 100]);

  useEffect(() => {
    if (universities.length > 0) {
      const percentiles = universities.map((college) => college.percentile);
      const minPercentile = Math.min(...percentiles);
      const maxPercentile = Math.max(...percentiles);
      setPercentileRange([minPercentile, maxPercentile]);
    }
  }, [universities]);

  return (
    <div>
      <div style={sliderStyles.container}>
        <ReactSlider
          min={percentileRange[0]}
          max={percentileRange[1]}
          value={percentileRange}
          onChange={(range) => setPercentileRange(range)}
          renderTrack={(props, state) => <div {...props} style={sliderStyles.track} />}
          renderThumb={(props, state) => <div {...props} style={sliderStyles.thumb} />}
        />
      </div>
      <div style={sliderStyles.rangeValues}>
        <span>{percentileRange[0]}</span>
        <span>{percentileRange[1]}</span>
      </div>
      <CardContainer>
        {Array.isArray(universities) && universities.length > 0 ? (
          universities
            .filter((college) => 
              college.percentile >= percentileRange[0] && college.percentile <= percentileRange[1]
            )
            .map((college) => (
              <UniversityCard
                key={college['_id']}
                name={college['College Name']}
                percentile={college['percentile']}
                branch={college['Branch Name']}
                city={college['District']}
                onApply={() => {
                  const url = college['Website URL'];
                  const formattedURL =
                    url.startsWith('http://') || url.startsWith('https://')
                      ? url
                      : `http://${url}`;
                  window.open(formattedURL, '_blank');
                }}
              />
            ))
        ) : (
          <p>No colleges found</p>
        )}
      </CardContainer>
    </div>
  );
};

export default UniversityCardList;
