import React from 'react';
import './SidePanel.css'; // Ensure you have a corresponding CSS file
import LoginModal from './LoginModal'; // Import the LoginModal component

function SidePanel({ isOpen, onClose, isLoggedIn, setIsLoggedIn, onLogout }) {
  const handleLogin = (user) => {
    // Set the login state to true and store user data if necessary
    localStorage.setItem('user', JSON.stringify(user));
    setIsLoggedIn(true);
    alert(`Welcome, ${user.name}!`); // Feedback on login
  };

  return (
    <div
      className={`fixed inset-0 z-50 transition-transform ${isOpen ? "translate-x-0" : "-translate-x-full"} bg-gray-100`}
      aria-hidden={!isOpen} // Accessibility
    >
      <div className="flex justify-between p-4 border-b">
        <h2 className="text-xl font-bold">{isLoggedIn ? "Profile" : "Login / Signup"}</h2>
        <button onClick={onClose} className="text-lg">X</button>
      </div>
      
      {isLoggedIn ? (
        <div className="p-4">
          <h3 className="text-lg">Welcome, {JSON.parse(localStorage.getItem('user')).name}!</h3>
          <button onClick={onLogout} className="mt-4 p-2 bg-red-500 text-white rounded">Logout</button>
        </div>
      ) : (
        <div className="p-4">
          {/* Render the LoginModal when the user is not logged in */}
          <LoginModal onLogin={handleLogin} />
        </div>
      )}
    </div>
  );
}

export default SidePanel;
