import React from 'react';
import './RangeSlider.css'; // Import the slider styles

const RangeSlider = ({ min, max, value, onChange }) => {
  const handleMinChange = (event) => {
    const minValue = Math.min(Number(event.target.value), value[1]);
    onChange([minValue, value[1]]);
  };

  const handleMaxChange = (event) => {
    const maxValue = Math.max(Number(event.target.value), value[0]);
    onChange([value[0], maxValue]);
  };

  return (
    <div className="slider-container">
      <div className="slider">
        <div
          className="progress"
          style={{
            left: `${((value[0] - min) / (max - min)) * 100}%`,
            width: `${((value[1] - value[0]) / (max - min)) * 100}%`
          }}
        ></div>
        <input
          type="range"
          min={min}
          max={max}
          value={value[0]}
          onChange={handleMinChange}
          className="range-slider"
        />
        <input
          type="range"
          min={min}
          max={max}
          value={value[1]}
          onChange={handleMaxChange}
          className="range-slider"
        />
      </div>
      <div className="range-values">
        <span>{value[0]}</span>
        <span>{value[1]}</span>
      </div>
    </div>
  );
};

export default RangeSlider;
